            &lt;div class=&quot;row-fluid&quot;&gt;
            &lt;div class=&quot;span3&quot;&gt;        
                &lt;div class=&quot;well&quot;&gt;
                    &lt;label&gt;A&lt;/label&gt;
                    &lt;textarea rows=&quot;10&quot;, cols=&quot;10&quot; id=&quot;A&quot;&gt;I think watir-webdriver is better than selenium-webdriver&lt;/textarea&gt;
                &lt;/div&gt;            
            &lt;/div&gt;
            &lt;div class=&quot;span3&quot;&gt;        
                &lt;div class=&quot;well&quot;&gt;
                    &lt;label&gt;B&lt;/label&gt;
                    &lt;textarea rows=&quot;10&quot;, cols=&quot;10&quot; id=&quot;B&quot;&gt;&lt;/textarea&gt;
                &lt;/div&gt;            
            &lt;/div&gt;
            &lt;/div&gt;        
        &lt;/body&gt;
