            &lt;script type=&quot;text/javascript&quot;&gt;
                $(document).ready(
                    function(){
                        $(&#39;.nav&#39;).find(&#39;li&#39;).click(function() {
                            $(this).parent().find(&#39;li&#39;).removeClass(&#39;active&#39;);
