# -*- coding: utf-8 -*- 
from selenium import webdriver
from time import sleep
import os
if &#39;HTTP_PROXY&#39;in os.environ: del os.environ[&#39;HTTP_PROXY&#39;]

dr = webdriver.Chrome()
file_path = &#39;file:///&#39; + os.path.abspath(&#39;attribute.html&#39;)

dr.get(file_path)

link = dr.find_element_by_id(&#39;tooltip&#39;)

sleep(1)
# 获得tooltip的内容
